sect3.screen:
send host-name "myname.my.domain";                 <=== ここに
                                                        ホスト名を書く
send dhcp-client-identifier "myident";             <=== ここにホスト
                                                        識別子を書く
							(これは myname と同じ
							 であることが多い).
request subnet-mask, broadcast-address, routers,
    domain-name-servers;

timeout 30;
retry 60;
select-timeout 5;

script "/sbin/dhclient-script";

lease {
  interface "sn0";                               <=== ここにインターフェース
                                                      デバイスを書く
  option host-name "myname.my.domain";           <=== ここに
                                                      ホスト名を書く
  option subnet-mask 255.255.255.0;
  option domain-name "my.domain";                <=== ここに
                                                      ドメイン名を書く
  option domain-name-servers 127.0.0.1;
  renew 2 2000/1/12 00:00:01;
  rebind 2 2000/1/12 00:00:01;
  expire 2 2000/1/12 00:00:01;
}

&rprompt; cat /etc/dhclient-enter-hooks
make_resolv_conf() {
        echo "doing nothing to resolv.conf"
}
